public class test {
    public static void main(String[] args) {
        // byte[] decode = Base64.decode("");
        // FileUtil.writeBytes(decode, "C:\\Users\\ggk911\\Desktop\\1234.png");

        // 短链接测试
        String encode = BaseConversionUtil.base62Encode(Long.parseLong("1717745894050250752"));
        System.out.println(encode);
        long decode = BaseConversionUtil.base62Decode(encode);
        System.out.println(decode);

        String base64 = "";
        System.out.println(Arrays.toString(Base64.decode(base64)));
        System.out.println(Arrays.toString(Base64Decoder.decode(base64)));

    }

    @Test
    @SneakyThrows
    public void decodeTest() {
        String data = "ccit";
        String toBase64String = Base64.toBase64String(data.getBytes(StandardCharsets.UTF_8));
        System.out.println(toBase64String);
        System.out.println(new String(Base64.decode(toBase64String)));

        System.out.println("--------------------------------------------------------");

        byte[] encode = Base64.encode(data.getBytes(StandardCharsets.UTF_8));
        System.out.println(new String(encode));
        System.out.println(new String(Base64.decode(encode)));
    }
}
